	case
		when ophal=1 then ''1''
		when ophal=2 and not (kmaak=21 or kl.koulutusala2002=''Muu koulutus'') then ''2''
		when ophal=2 then ''3''
		else ''-1'' 
	end 
	as nvarchar(2))*/
