ALTER PROCEDURE [dbo].[Laske_VipunenTK_laatu] AS

/*
CSC Jarmo 22.4.2015

Execute  VipunenTK_DW.dbo.Laske_VipunenTK_laatu

Ajo kestää 2 tuntia. Se päivittää taulun VipunenTK_laatu.
Sen tuloksia voi kysellä näkymällä v_VipunenTK_laatu

CSC Jarmo 22.6.2015
NULL-arvot muutetaan VipunenTK_laatu-tauluun arvoksi -3, jotta group by löytää ne
*/

  BEGIN 
  SET nocount  ON
    DECLARE  @imax INT, 
             @i    INT 
    DECLARE  	@Kanta [nvarchar](128),
	@Skeema [sysname],
	@Taulu [sysname],
	@Tyyppi [varchar](10),
	@Sarake [sysname]
-- The RowID identity column will be used for loop control
-- MSSQL declare table variable 
    DECLARE  @VanhatTaulut  TABLE( 
    RowID       INT    IDENTITY ( 1 , 1 ),	
	[TABLE_CATALOG] [nvarchar](128),
	[TABLE_SCHEMA] [sysname],
	[TABLE_NAME] [sysname],
	[TABLE_TYPE] [varchar](10)
     ) 
    DECLARE  @UudetTaulut  TABLE( 
    RowID       INT    IDENTITY ( 1 , 1 ),	
	[TABLE_CATALOG] [nvarchar](128),
	[TABLE_SCHEMA] [sysname],
	[TABLE_NAME] [sysname],
	[TABLE_TYPE] [varchar](10)
     ) 
    DECLARE  @VanhatSarakkeet  TABLE( 
    RowID       INT    IDENTITY ( 1 , 1 ),	
	[TABLE_CATALOG] [nvarchar](128),
	[TABLE_SCHEMA] [sysname],
	[TABLE_NAME] [sysname],
	[TABLE_TYPE] [varchar](10),
	[COLUMN_NAME] [sysname]
     ) 
    DECLARE  @UudetSarakkeet  TABLE( 
    RowID       INT    IDENTITY ( 1 , 1 ),	
	[TABLE_CATALOG] [nvarchar](128),
	[TABLE_SCHEMA] [sysname],
	[TABLE_NAME] [sysname],
	[TABLE_TYPE] [varchar](10),
	[COLUMN_NAME] [sysname]
     ) 

--Hae uuden kannan tarkistussummat
	Truncate table [VipunenTK_DW].[dbo].[VipunenTK_laatu]

    INSERT @UudetSarakkeet
    SELECT   taulut.TABLE_CATALOG, 
             taulut.TABLE_SCHEMA,
			 taulut.TABLE_NAME,
			 taulut.TABLE_TYPE,
			 sarakkeet.COLUMN_NAME
    FROM     [VipunenTK].information_schema.tables taulut
	JOIN [VipunenTK].INFORMATION_SCHEMA.COLUMNS sarakkeet
      on taulut.TABLE_NAME = sarakkeet.TABLE_NAME
    WHERE sarakkeet.DATA_TYPE in ('int') and Right(sarakkeet.COLUMN_NAME,2) = 'id' and left(taulut.TABLE_NAME,1) = 'f'
    ORDER BY TABLE_NAME, COLUMN_NAME 

    SET @imax = @@ROWCOUNT 
    SET @i = 1 
     
    WHILE (@i <= @imax) 
      BEGIN 
        SELECT @Kanta = TABLE_CATALOG, 
               @Skeema = TABLE_SCHEMA,
			   @Taulu = TABLE_NAME,
			   @Tyyppi = TABLE_TYPE,
			   @Sarake = COLUMN_NAME
        FROM   @UudetSarakkeet
        WHERE  RowID = @i 
         
        PRINT 
		'Insert into VipunenTK_DW.dbo.VipunenTK_laatu 
		 Select Taulu='''+@Taulu+''', Sarake='''+@Sarake+''', Arvo=isnull(Case when ['+@Sarake+']>=0 then 0 else ['+@Sarake+'] end, -3), Lkm=count(['+@Sarake+']) 
		 from '+@Kanta+'.'+@Skeema+'.['+@Taulu+'] group by isnull(Case when ['+@Sarake+']>=0 then 0 else ['+@Sarake+'] end, -3)'
        EXEC( 'Insert into VipunenTK_DW.dbo.VipunenTK_laatu 
		 Select Taulu='''+@Taulu+''', Sarake='''+@Sarake+''', Arvo=isnull(Case when ['+@Sarake+']>=0 then 0 else ['+@Sarake+'] end, -3), Lkm=count(['+@Sarake+']) 
		 from '+@Kanta+'.'+@Skeema+'.['+@Taulu+'] group by isnull(Case when ['+@Sarake+']>=0 then 0 else ['+@Sarake+'] end, -3)'
         )

        SET @i = @i + 1 
      END -- WHILE

--Palauta laatu virhe%:n mukaan lajiteltuna, kun proseduuri on ajettu

Select * from VipunenTK_DW.dbo.v_VipunenTK_laatu
order by 
	cast(case when cast(virhe as float)=0.0 then 0.0 else cast(virhe as float)/Cast((normaali+tuntematon+virhe+muut) as float)*100.0 end as float) desc, 
	cast(case when cast(tuntematon as float) = 0.0 then 0.0 else cast(tuntematon as float)/Cast((normaali+tuntematon+virhe+muut) as float)*100.0 end as float) desc, 
	taulu, sarake


  END 



GO
