      ,[lkm_tytot]
      ,[vuosiluokka_koodi]=f.vuosiluokka_koodi
      ,[kieltenmäärä_koodi]=f.kieltenmäärä_koodi
      ,lkm_kielivalinnat
      ,[oppilaitoksensijainti_historia_koodi]
      ,[vuosiluokka_id]=isnull(d1.id, -2)
      ,[tilv_date]
      ,[aineisto]
      ,[laatu_id]=0
