TRUNCATE TABLE VipunenTK.dbo.f_4_9_Vaestorakenne_kunnittain;

INSERT INTO VipunenTK.dbo.f_4_9_Vaestorakenne_kunnittain


SELECT
	 f.tilastointivuosi
	,f.sukupuoli
	,sukupuoli_id = coalesce(d1.id,-1)
	,f.kotikunta
	,kotikunta_id = coalesce(d2.id,-1)
	,f.aidinkieli_versio2
	,aidinkieli_versio2_id = coalesce(d3.id,-1)
	,f.kansalaisuus_versio1
	,kansalaisuus_versio1_id = coalesce(d4.id,-1)
	,f.maanosa 	
	,maanosa_id = coalesce(d5.id,-1)
	,f.pohjoismaa
	,pohjoismaa_id = coalesce(d6.id,-1)
	,f.eumaa
	,eumaa_id = coalesce(d7.id,-1)
	,f.etamaa
	,etamaa_id = coalesce(d8.id,-1)
	,f.ika
	,ika_id = coalesce(d9.id,-1)
	,f.lukumaara

 
FROM VipunenTK_DW.dbo.v_f_4_9_Vaestorakenne_kunnittain2 AS f

LEFT JOIN VipunenTK..d_sukupuoli AS d1 ON d1.sukupuoli_koodi=f.sukupuoli 
LEFT JOIN VipunenTK..d_alueluokitus AS d2 ON d2.alueluokitus_avain=f.kotikunta
LEFT JOIN VipunenTK..d_aidinkieli_versio2 AS d3 ON d3.aidinkieli_versio2_koodi=f.aidinkieli_versio2 
LEFT JOIN VipunenTK..d_kansalaisuus_versio1 AS d4 ON d4.kansalaisuus_versio1_koodi=f.kansalaisuus_versio1 
LEFT JOIN VipunenTK..d_valtio AS d5 ON d5.valtio_avain='M'+f.maanosa 
LEFT JOIN VipunenTK..d_pohjoismaa AS d6 ON d6.pohjoismaa_koodi=f.pohjoismaa
LEFT JOIN VipunenTK..d_valtio AS d7 ON d7.valtio_avain='EU'+f.eumaa
LEFT JOIN VipunenTK..d_valtio AS d8 ON d8.valtio_avain='ETA'+f.etamaa
LEFT JOIN VipunenTK..d_ika AS d9 ON d9.ika_avain=(case when f.ika in ('95','96','97','98','99') then f.ika+'v2' else f.ika end)




