      ,tyonantajasektori2_id = coalesce(d11.id, -2)
	  ,tyonantajasektori2_ed_id = coalesce(d18.id, -2)
	  ,tyonantajasektori2_tthv_id = coalesce(d20.id, -2)
	  ,tyonantajasektori2_ed_tthv_id = coalesce(d21.id, -2)
