	  ,toimiala_tthv2_sama_kuin_ed_vuonna_id = coalesce(d24.id, -2)
	  ,yrityksen_koko_id = coalesce(d25.id, -1)
	  ,tyonantaja_vaihtunut_id = coalesce(d26.id,-1)
      ,f.[lkm]
	  --Otetaan avaimet talteen testausta varteen
